from selenium import webdriverfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.common.action_chains import ActionChainsimport timedriver = webdriver.Chrome()driver.maximize_window()driver.get("file:///Users/raniaburaia/PycharmProjects/Captain-Fix/ActionChainsEx.Html")# Click Meclick_box = driver.find_element(By.ID, "click-box")click_box.click()time.sleep(2)message = driver.find_element(By.ID, "message-box")print(message.text)# Double Click Medouble_box = driver.find_element(By.ID, "double-box")actions = ActionChains(driver)actions.double_click(double_box).perform()time.sleep(1)print(message.text)# Right Click Meright_box = driver.find_element(By.ID, "right-box")actions.context_click(right_box).perform()time.sleep(2)print(message.text)# Hold Mehold_box = driver.find_element(By.ID, "hold-box")actions.click_and_hold(hold_box).perform()time.sleep(2)print(message.text)actions.release().perform()time.sleep(2)print(message.text)# Drag Medrag_element = driver.find_element(By.ID, "drag1")dropzone = driver.find_element(By.ID, "dropzone")actions.click_and_hold(drag_element).perform()time.sleep(2)print("⏳ Dragging started...")actions.move_to_element(dropzone).perform()actions.release().perform()time.sleep(2)print("✔ Drag and Drop action performed!")# Hover Over Mehover_target = driver.find_element(By.ID, "hover-target")actions.move_to_element(hover_target).perform()time.sleep(2)print(message.text)# Input texttext_input = driver.find_element(By.ID, "text-input")text_input.send_keys("Test Input")time.sleep(2)