import unittestimport timefrom cmath import acoshfrom selenium import webdriverfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.common.action_chains import ActionChainsclass MyTestCase(unittest.TestCase):    def setUp(self):        self.driver = webdriver.Chrome()        self.driver.get("file:///Users/raniaburaia/Desktop/certificate/Captain-Fix/ActionChainsEx.Html")        self.driver.maximize_window()        self.action = ActionChains(self.driver)        time.sleep(2)    def tearDown(self):        self.driver.quit()    def test_click(self):        driver = self.driver        action = self.action        click_box = driver.find_element(By.ID, "click-box")        action.click(click_box).perform()        time.sleep(2)    def test_double_click(self):        driver = self.driver        action = self.action        double_box = driver.find_element(By.ID, "double-box")        action.double_click(double_box).perform()        time.sleep(2)    def test_right_click(self):        driver = self.driver        action = self.action        right_box = driver.find_element(By.ID, "right-box")        action.context_click(right_box).perform()        time.sleep(2)    # def test_click_and_hold(self):    #     hold_box = self.driver.find_element(By.ID, "hold-box")    #     self.actions.click_and_hold(hold_box).pause(1).release().perform()    #     time.sleep(1)    def test_Click_and_Hold(self):        driver = self.driver        action = self.action        hold_box = driver.find_element(By.ID, "hold-box")        action.click_and_hold(hold_box).pause(2).release(hold_box).perform()        time.sleep(1)    def test_drag_and_drop(self):        driver = self.driver        action = self.action        drag_item = driver.find_element(By.ID, "drag1")        drop_zone = driver.find_element(By.ID, "dropzone")        action.drag_and_drop(drag_item, drop_zone).perform()        time.sleep(2)    def test_hover(self):        driver = self.driver        action = self.action        hover_target = driver.find_element(By.ID, "hover-target")        action.move_to_element(hover_target).perform()        time.sleep(2)    def test_send_keys(self):        text_input = self.driver.find_element(By.ID, "text-input")        text_input.send_keys("Hello World!")        time.sleep(2)if __name__ == '__main__':    unittest.main()