from selenium import webdriverfrom selenium.webdriver.common.action_chains import ActionChainsfrom selenium.webdriver.common.by import Byimport time# Initialize the driverdriver = webdriver.Chrome()driver.get('file:///Users/raniaburaia/PycharmProjects/Captain-Fix/ActionChainsEx.Html')driver.maximize_window()# Test Click Me buttonclick_me_button = driver.find_element(By.ID, "clickMe")click_me_button.click()time.sleep(1)print(driver.find_element(By.ID, "message").text)# Test Double Click Me buttondouble_click_me_button = driver.find_element(By.ID, "doubleClickMe")ActionChains(driver).double_click(double_click_me_button).perform()time.sleep(1)print(driver.find_element(By.ID, "message").text)# Test Right Click Me buttonright_click_me_button = driver.find_element(By.ID, "rightClickMe")ActionChains(driver).context_click(right_click_me_button).perform()time.sleep(1)print(driver.find_element(By.ID, "message").text)# Test Hold Me buttonhold_me_button = driver.find_element(By.ID, "holdMe")ActionChains(driver).click_and_hold(hold_me_button).perform()time.sleep(1)print(driver.find_element(By.ID, "message").text)# Release the Hold Me buttonActionChains(driver).release(hold_me_button).perform()time.sleep(1)print(driver.find_element(By.ID, "message").text)# Test Drag Me elementdrag_me_element = driver.find_element(By.ID, "dragMe")ActionChains(driver).drag_and_drop(drag_me_element, driver.find_element(By.ID, "dropHere")).perform()time.sleep(1)print(driver.find_element(By.ID, "message").text)# Test Hover Over Me elementhover_over_me_element = driver.find_element(By.ID, "hoverOverMe")ActionChains(driver).move_to_element(hover_over_me_element).perform()time.sleep(1)# Close the driverdriver.quit()