from selenium import webdriverfrom selenium.webdriver.common.action_chains import ActionChainsfrom selenium.webdriver.common.by import Byimport timedriver = webdriver.Chrome()driver.maximize_window()driver.get("file:///Users/raniaburaia/PycharmProjects/Captain-Fix/ActionChainsEx.Html")actions = ActionChains(driver)# 1. Click #click-boxclick_box = driver.find_element(By.ID, "click-box")click_box.click()time.sleep(1)# 2. Double click #double-boxdouble_box = driver.find_element(By.ID, "double-box")actions.double_click(double_box).perform()time.sleep(1)# 3. Right click #right-boxright_box = driver.find_element(By.ID, "right-box")actions.context_click(right_box).perform()time.sleep(1)# 4. Click and hold #hold-box, then releasehold_box = driver.find_element(By.ID, "hold-box")actions.click_and_hold(hold_box).perform()time.sleep(1)  # Hold for 1 secondactions.release(hold_box).perform()time.sleep(1)# 5. Drag #drag1 and drop on #dropzonedrag1 = driver.find_element(By.ID, "drag1")dropzone = driver.find_element(By.ID, "dropzone")actions.click_and_hold(drag1).move_to_element(dropzone).release().perform()time.sleep(1)# 6. Hover over #hover-targethover_target = driver.find_element(By.ID, "hover-target")actions.move_to_element(hover_target).perform()time.sleep(1)# 7. Send keys to #text-inputtext_input = driver.find_element(By.ID, "text-input")text_input.send_keys("Automation Test")time.sleep(3)driver.quit()